import axios from 'axios';
import React, { useEffect, useState } from 'react'
import { useParams } from 'react-router-dom'

const EditEquipment = () => {

    const {id}=useParams();
    const [name,setName]=useState("");
    const [type,setType]=useState("");
    const [status,setStatus]=useState("");
    const [lastcleandate,setLastCleanDate]=useState("");


    const [equipment,setEquipment]=useState(null)

    useEffect(()=>{
        axios.get(`http://localhost:3000/api/equipment/${id}`)
        .then(response=>setEquipment(response.data))
        .catch(err=>console.log(err));
    },[id])

    function handleSubmit(e){
        e.preventDefault();
        axios.put(`http://localhost:3000/api/equipment`,{
            name,type,status,lastcleandate
        })
        .then(response=>console.log(response.data))
        .catch(err=>console.log(err));
    }

  return (
   <div>
        <form onSubmit={handleSubmit}>
                <label>Name:</label>
                <input type='text' placeholder='Name' value={equipment.name} onChange={(e)=>setName(e.target.value)}/><br></br>
                <label>Type:</label>
                <select onChange={(e)=>setType(e.target.value)}  value={equipment.type}>
                    <option value="">--select--</option>
                    <option value="Machine">Machine</option>
                    <option value="Vessel">Vessel</option>
                    <option value="Tank">Tank</option>
                    <option value="Mixer">Mixer</option>
                </select><br></br>
                <label>Status:</label>
                <select onChange={(e)=>setStatus(e.target.value)} value={equipment.status}>
                    <option value="">--select--</option>
                    <option value="active">Active</option>
                    <option value="inactive">InActive</option>
                    <option value="under maintenance">Under Maintenance</option>
                </select><br></br>
                <label>LastCleanedDate:</label>
                <input type='Date' onChange={(e)=>setLastCleanDate(e.target.value)} value={equipment.lastcleandate}/>
                <div>
                    <button type='submit'>Update Equipment</button>
                    <Link to="/api/equipment">View</Link>
                </div>
        </form>
        </div>
  )
}

export default EditEquipment
