import axios from 'axios'
import React, { useEffect, useState } from 'react'
import { Link, useParams } from 'react-router-dom'
import './App.css'

const Edit = () => {


  const { id }=useParams();
  const [equipment,setEquipment]=useState({
    name:"",
    type:"",
    status:"",
    lastcleandate:""
  });

  console.log(id);

  useEffect(()=>{
    axios.get(`http://localhost/api/equipment/${id}`)
    .then(resp=>{
      const data=resp.data;
      setEquipment({
        name:data.name,
        type:data.type,
        status:data.status,
        lastcleandate:data.lastcleandate?.slice(0,10)
      });
    })
    .catch(err=>console.log(err));
  },[id]);

  console.log(equipment);

  function handleChange(e){
    e.preventDefault();
    const {name, value}=e.target;
    setEquipment(prev=>({
      ...prev,
      [name]:value
    }))
  }

  console.log(equipment);
  return (
    <div className='home-div'>
       <form className='form'>
            <label>Name:</label>
            <input type='text' name='name' placeholder='Equipment Name' className='input' value={equipment.name} onChange={handleChange}/><br></br>
             <label>Type:</label>
             <select  className='input' value={equipment.type} onChange={handleChange}>
                <option value="">--select--</option>
                <option value="machine">machine</option>
                <option value="vessel">vessel</option>
                <option value="tank">tank</option>
                <option value="mixer">mixer</option>
             </select><br></br>
             <label>Status:</label>
             <select  className='input' value={equipment.status} onChange={handleChange}>
                <option value="">--select--</option>
                <option value="active">active</option>
                <option value="inactive">inactive</option>
                <option value="under-maintenance">under-maintenance</option>
             </select><br></br>
             <label>Last Cleaned Date:</label>
             <input type='date' className='input' value={equipment.lastcleandate} onChange={handleChange}/>
             <div className='btns'>
                <Link to={'/api/equipment'} style={{backgroundColor:"green",color:"white"}}>Update Equipment</Link>
                <Link to={'/api/equipment'} style={{backgroundColor:"blue",color:"white"}}>View Equipments</Link>
             </div>
        </form>
    </div>
  )
}

export default Edit
