import axios from 'axios'
import React, { useEffect, useState } from 'react'
import { Link, useParams } from 'react-router-dom'
import './App.css'

const Edit = () => {

  const { id }=useParams();
  const [equipment,setEquipment]=useState({
    name:"",
    type:"",
    status:"",
    lastcleandate:""
  });

  useEffect(()=>{
    axios.get(`http:localhost:3001/api/equipment/${id}`)
    .then(resp=>{
      setEquipment({
        name:resp.data.name,
        type:resp.data.type,
        status:resp.data.status,
        lastcleandate:resp.data.lastcleandate?.slice(0,10)
      });
    })
    .catch(err=>console.log(err));
  },[id]);


  return (
    <div className='home-div'>
       <form className='form'>
            <label>Name:</label>
            <input type='text' placeholder='Equipment Name' className='input' value={equipment.name}/><br></br>
             <label>Type:</label>
             <select  className='input' value={equipment.type}>
                <option value="">--select--</option>
                <option value="machine">machine</option>
                <option value="vessel">vessel</option>
                <option value="tank">tank</option>
                <option value="mixer">mixer</option>
             </select><br></br>
             <label>Status:</label>
             <select  className='input' value={equipment.status}>
                <option value="">--select--</option>
                <option value="active">active</option>
                <option value="inactive">inactive</option>
                <option value="under-maintenance">under-maintenance</option>
             </select><br></br>
             <label>Last Cleaned Date:</label>
             <input type='date' className='input' value={equipment.lastcleandate}/>
             <div className='btns'>
                <Link to={'/api/equipment'} style={{backgroundColor:"green",color:"white"}}>Update Equipment</Link>
                <Link to={'/api/equipment'} style={{backgroundColor:"blue",color:"white"}}>View Equipments</Link>
             </div>
        </form>
    </div>
  )
}

export default Edit
