import axios from 'axios';
import React, { useEffect, useState } from 'react'
import { Link, useParams } from 'react-router-dom'

const Edit = () => {

  const { id } =useParams();

  const [name,setName]=useState("");
  const [type,setType]=useState("");
  const [status,setStatus]=useState("");
  const [lastcleandate,setLastCleandate]=useState("");

  useEffect(()=>{
    axios.get(`http://localhost:3001/api/equipment/${id}`)
    .then(resp=>{
      console.log(resp.data);
       // setName(resp.data.name);
      // setType(resp.data.type);
      // setStatus(resp.data.status);
      // lastcleandate(resp.data.lastcleandate);
    })
    .catch(err=>console.log(err));
  },[id])

console.log(name,type);



  return (
    <div className='home-div '>
        <form className='form'>
            <label>Name:</label>
            <input type='text' placeholder='Equipment Name' className='input'/><br></br>
             <label>Type:</label>
             <select  className='input'>
                <option value="">--select--</option>
                <option value="machine">machine</option>
                <option value="vessel">vessel</option>
                <option value="tank">tank</option>
                <option value="mixer">mixer</option>
             </select><br></br>
             <label>Status:</label>
             <select  className='input'>
                <option value="">--select--</option>
                <option value="active">active</option>
                <option value="inactive">inactive</option>
                <option value="under-maintenance">under-maintenance</option>
             </select><br></br>
             <label>Last Cleaned Date:</label>
             <input type='date' className='input'/>
             <div className='btns'>
                <Link to={"/api/updateequipment"} style={{backgroundColor:"green",color:"white"}}>Update Equipment</Link>
                <Link to={"/api/equipment"} style={{backgroundColor:"blue",color:"white"}}>View Equipments</Link>
             </div>
        </form>
    </div>
  )
}

export default Edit
