import axios from 'axios';
import React, { useState } from 'react'
import { Link, useParams } from 'react-router-dom';

const DeleteEquipment = () => {

    const {id}=useParams();

    const [equipment,setEquipment]=useState([]);

    useEffect(()=>{
        const deleteData=()=>{
            axios.delete(`http://localhost:3000/api/equipment/${id}`)
            .then(response=>console.log(response))
            .catch(err=>console.log(err));
        }
        deleteData();
       const fetchData = async(e)=>{
        await axios.get('http://localhost:3000/api/equipment')
        .then(response=>setEquipment(response.data))
        .catch(err=>console.log(err));
       };
       fetchData();
    },[id])

  return (
    <div>
        <div style={{backgroundColor:"green",color:"white",width:"200px",height:"40px"}}>
            <Link to="/" style={{color:"white",display:"flex",justifyContent:"center",paddingTop:"10px",textDecoration:"none"}}>Add Equipment</Link>
        </div>
        <table>
            <thead>
                <tr>
                    <th>Id</th>
                    <th>Name</th>
                    <th>Type</th>
                    <th>Status</th>
                    <th>LastCleanedDate</th>
                    <th>Action</th>
                </tr>
            </thead>
            <tbody>
                {equipment.map((eq)=>(
                    <tr key={eq.id}>
                        <td>{eq.id}</td>
                        <td>{eq.name}</td>
                        <td>{eq.type}</td>
                        <td>{eq.status}</td>
                        <td>{eq.lastcleandate}</td>
                        <div style={{display:"flex",justifyContent:"center",columnGap:"10px",alignItems:"center",border: "1px solid black",padding: "10px"}}>
                            <Link to={`/api/equipment/${eq.id}`}>Edit</Link>
                            <Link to={`/api/equipment/${eq.id}`}>Delete</Link>
                        </div>
                    </tr>
                ))}
            </tbody>
        </table>
    </div>
  )
}

export default DeleteEquipment
