import axios from 'axios';
import React, { useEffect, useState } from 'react'
import { Link, useNavigate, useParams } from 'react-router-dom'

const Edit = () => {

  const navigate=useNavigate();

  const { id } =useParams();

  const [name,setName]=useState("");
  const [type,setType]=useState("");
  const [status,setStatus]=useState("");
  const [lastcleandate,setLastCleandate]=useState("");

  useEffect(()=>{
    axios.get(`http://localhost:3001/api/equipment/${id}`)
    .then(resp=>{
      const data=resp.data;
      console.log(data[0]);
      setName(data[0].name);
      setType(data[0].type);
      setStatus(data[0].status);
      setLastCleandate(data[0].lastcleandate)
    })
    .catch(err=>console.log(err));
  },[id])

  function handleSubmit(e){
    e.preventDefault(); 
    axios.put(`http://localhost:3001/api/updateequipment/${id}`,{
      name,type,status,lastcleandate
    })
    .then(resp=>{
      alert("Equipment Updated Successfully");
      console.log(resp);
      navigate('/api/equipment')
    })
    .catch(err=>console.log(err));
  }

  console.log(name);
  return (
    <div className='home-div '>
        <form className='form'>
            <label>Name:</label>
            <input type='text' placeholder='Equipment Name' className='input' value={name} onChange={(e)=>setName(e.target.value)}/><br></br>
             <label>Type:</label>
             <select  className='input' value={type} onChange={(e)=>setType(e.target.value)}>
                <option value="">--select--</option>
                <option value="machine">machine</option>
                <option value="vessel">vessel</option>
                <option value="tank">tank</option>
                <option value="mixer">mixer</option>
             </select><br></br>
             <label>Status:</label>
             <select  className='input' value={status} onChange={(e)=>setStatus(e.target.value)}>
                <option value="">--select--</option>
                <option value="active">active</option>
                <option value="inactive">inactive</option>
                <option value="under-maintenance">under-maintenance</option>
             </select><br></br>
             <label>Last Cleaned Date:</label>
             <input type='date' className='input' onChange={(e)=>setLastCleandate(e.target.value)}/>
             <div className='btns'>
                <Link onClick={handleSubmit} style={{backgroundColor:"green",color:"white"}}>Update Equipment</Link>
                <Link to={"/api/equipment"} style={{backgroundColor:"blue",color:"white"}}>View Equipments</Link>
             </div>
        </form>
    </div>
  )
}

export default Edit
