import axios from 'axios';
import React, { useEffect, useState } from 'react'
import { Link, useParams } from 'react-router-dom'

const Edit = () => {

  const { id } =useParams();

  const [equipment,setEquipment]=useState({
    name:"",
    type:"",
    status:"",
    lastcleandate:""
  });

  useEffect(()=>{
    axios.get(`http://localhost:3001/api/equipment/${id}`)
    .then(resp=>{
      const data=resp.data;
      setEquipment({
        name:data.name,
        type:data.type,
        status:data.status,
        lastcleandate:data.lastcleandate?.slice(0,10)
      });
    })
    .catch(err=>console.log(err));
  })

  return (
    <div className='home-div '>
        <form className='form'>
            <label>Name:</label>
            <input type='text' placeholder='Equipment Name' className='input' onChange={(e)=>setName(e.target.value)}/><br></br>
             <label>Type:</label>
             <select  className='input' onChange={(e)=>setType(e.target.value)}>
                <option value="">--select--</option>
                <option value="machine">machine</option>
                <option value="vessel">vessel</option>
                <option value="tank">tank</option>
                <option value="mixer">mixer</option>
             </select><br></br>
             <label>Type:</label>
             <select  className='input' onChange={(e)=>setStatus(e.target.value)}>
                <option value="">--select--</option>
                <option value="active">active</option>
                <option value="inactive">inactive</option>
                <option value="under-maintenance">under-maintenance</option>
             </select><br></br>
             <label>Last Cleaned Date:</label>
             <input type='date' className='input' onChange={(e)=>setLastCleandate(e.target.value)}/>
             <div className='btns'>
                <Link to={"/api/addequipment"}style={{backgroundColor:"green",color:"white"}}>Update Equipment</Link>
                <Link to={"/api/equipment"} style={{backgroundColor:"blue",color:"white"}}>View Equipments</Link>
             </div>
        </form>
    </div>
  )
}

export default Edit
